package dynamicProgramming.LCS;

public class LongestPalindrome {
    public static String longestPalindrome(String s) {
        int m = s.length();
        boolean dp[][] = new boolean[m][m];
        StringBuilder result = new StringBuilder();

        for (int gap = 0; gap < m; gap++) {
            for (int i = 0, j = i + gap; j < m; i++, j++) {
                if (gap == 0) {
                    dp[i][j] = true;
                } else if (gap == 1) {
                    dp[i][j] = s.charAt(i) == s.charAt(j);
                } else {
                    dp[i][j] = dp[i + 1][j - 1] && (s.charAt(i) == s.charAt(j));
                }

                if (dp[i][j]) {
                    String sb = s.substring(i, j + 1);
                    if (sb.length() > result.length()) {
                        result = new StringBuilder(sb);
                    }
                }
            }
        }
        //printMat(dp);
        return result.toString();
    }

    public static void printMat(boolean dp[][]) {
        for (int i = 0; i < dp.length; i++) {
            for (int j = 0; j < dp.length; j++) {
                System.out.print(dp[i][j] + " | ");
            }

            System.out.println();
        }
    }

    public static void main(String[] args) {
        //System.out.println(longestPalindromeDPStr("civilwartestingwhetherthatnaptionoranynartionsoconceivedandsod"));
        //System.out.println(longestPalindrome(""));
        //String a="";
        String a = "aacabdkacaa";
        System.out.println(longestPalindrome(a));

    }
}
